mapper.select:
  select
  a.id,a.title,a.classId,a.content,a.comeFrom,a.addDate,a.updateDate,a.isDel,a.imageUrl,a.description,b.name as className
  from newsInfo a left join classInfo b on a.classId=b.id
  where isDel=0

      and title like concat('%',#{title},'%')


      and addDate>=#{start}



and addDate<#{end}



      and a.classId=#{classId}

  order by a.addDate desc
  limit #{offset},#{pageSize}

  select
  count(0)
  from newsInfo
  where isDel=0

      and title like concat('%',#{title},'%')


      and addDate>=#{start}



and addDate<#{end}



      and classId=#{classId}


        select id,title from newsinfo where id in
(select max(t.id) as id from (
select case when sign(id-#{id})<0 then min(id) end as id from newsinfo where classid=#{classId} and id<>#{id} group by id
) t
union all
select min(t.id) as id from (
select case when sign(id-#{id})>0 then min(id) end as id from newsinfo where classid=#{classId} and id<>#{id} group by id
) t
)
